$:/plugins/jcose/MegaMenu/styles.text:
.mmenu-container {
  width: 100%;
  height: 400px;
  border: 2px solid gray;
  border-style: ridge;
  display:flex;
  flex-direction: column;
}

.mmenu-topbar {
  min-height: 30px;
  background-color: <<mmenustyle headerbg>>;
  color: <<mmenustyle headerfg>>;
  display: flex;
  flex-direction: row;
  padding-top: 0.2em;
  border-bottom: 1px solid darkgray;
}

.mmenu-title {
  flex-grow: 1;
  width: 12em;
  align-content: center;
  vertical-align: middle;
  font-style: italic;
  font-weight: bold;
  padding-left: 1em;
}

.mmenu-top-dock {
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  width: 100%;
  font-size: smaller;
}

.mmenu-top-dock-item {
  font-weight: bold;
}

.mmenu-bottom-container {
  display: flex;
  flex-grow: 1;
  flex-direction: row;
  height: 0;
}

.mmenu-leftbar {
  background-color: <<mmenustyle leftbarbg>>;
  color: <<mmenustyle leftbarfg>>;
  height: 100%;
  min-width: 35%;
  border-right: 1px solid lightgray;
  display:flex;
  flex-direction: column;
}

.mmenu-left-header {
  font-weight: bold;
  border-bottom: 1px solid lightgray;
  padding: 0.5em 0.4em;
  min-height: 2em;
  background-color: <<mmenustyle leftbarheadbg>>;
  color: <<mmenustyle leftbarheadfg>>;
}

.mmenu-left-list {
  display: flex;
  flex-direction: column;
  /*align-items: flex-end;*/
  overflow-y: auto;
  padding: 0 0.4em;
  flex-grow: 1;
}

.mmenu-rightbar {
  background-color: <<mmenustyle rightbarbg>>;
  color: <<mmenustyle rightbarfg>>;
  height: 100%;
  flex-grow: 1;
  display: inline-flex;
  justify-content: left;
  flex-wrap: wrap;
  overflow-y: auto;
}

.mmenu-right-group {
  background-color: <<mmenustyle rightgroupbg>>;
  margin: 5px;
  padding: 5px;
  border: 1px solid #aaa;
  border-radius: 4px;
  animation: mmenu-slide-up 0.8s ease;
  box-shadow: 1px 1px 4px <<mmenustyle rightgroupshadow>>;
  flex: 1 0 auto;
  max-width: 98%;
  max-height: 98%;
}

.mmenu-right-group-header {
  font-weight: bold;
}

.mmenu-dock-item {
  padding: 0 1.5em 0 0.5em;
  transition: 0.5s ease-in-out;
  cursor: pointer;
  max-height: 4em;
  max-width: 100%;
  display: inline-block;
}

.mmenu-dock-item:hover {
  background-color: <<mmenustyle listhoverbg>>;
  transform: translateX(0.5em);
}

.mmenu-right-group-links {
  max-height: 93%;
  overflow-y: auto;
}


.mmenu-dock-item-sel {
  padding: 0 1.5em 0 0.5em;
  cursor: pointer;
  max-height: 4em;
  max-width: 100%;
  background-color: <<mmenustyle listselectbg>>;
  transform: translateX(0.5em);
  display: inline-block;
}

.mmenu-text-icon svg {
  width: 1em;
  height: 1em;
  fill: <<colour tiddler-link-foreground>>;
}

.mmenu-text-icon svg:hover {
  fill: <<mmenustyle linkbuttonhover>>;
}

@keyframes mmenu-slide-up {
    0% {
        opacity: 0;
        transform: scale(0.1);
    }
    100% {
        opacity: 1;
        transform: scale(1);
    }
}
