$:/plugins/linonetwo/mobile-imessage-lay.text:
.mobile-imessage-layout-container {
  height: 100vh;
}
.mobile-imessage-layout-container > p {
  margin: 0;
}

.mobile-imessage-layout-main-area {
  /** minus the mobile-imessage-layout-page-control-below-page height */
  height: calc(100vh - 45px);
  /** move input to bottom, and let chat near it */
  justify-content: flex-end;
  display: flex;
  flex-direction: column;
}

.mobile-imessage-layout-config-before-use {
  padding: 3rem;
  font-size: 1.5rem;
  line-height: 1.7;
  display: flex;
  flex-direction: column;
}
.mobile-imessage-layout-config-before-use .tc-edit-add-tag {
  margin: 20px 0;
}

.mobile-imessage-layout-page-control-below-page {
  padding: 10px;
  background-color: white;
  -webkit-backdrop-filter: blur(10px);

  position: fixed;
  bottom: 0;
  left: 0;
  z-index: 1000;
  width: 100%;
}
.mobile-imessage-layout-page-control-below-page .tc-page-controls {
  margin: 0;

  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-evenly;
}
