$:/plugins/linonetwo/template-list/style.text:
.linonetwo-template-list-item-container {
	height: 249px;
	width: 100%;
	border-radius: 3px;
	background: #white; box-shadow: rgb(15 15 15 / 20%) 0px 0px 0px 1px, rgb(15 15 15 / 20%) 0px 2px 4px;
	overflow: hidden;
	user-select: none;
	cursor: pointer;
	position: static;
	display: flex;
	flex-direction: column;
}

.linonetwo-template-list-item-container p {
	margin: 0;
}
