type: application/javascript
module-type: filteroperator
Filter operator for return a random title in the list.
\*/
(function () {

    /*jslint node: true, browser: true */
    /*global $tw: false */
    "use strict";

    /*
    Export our filter function
    */
    exports.sortrandom = function (source, operator, options) {
        var results = [];

        source(function (tiddler, title) {
            results.push(title);
        });

        results = results.sort(function () {
            return Math.random() - 0.5;
        });

        return results;
    };

})();

$:/plugins/oflg/fishing/macros/fsrs.js.text:
/*\
title: $:/plugins/oflg/fishing/macros/fsrs.js
type: application/javascript
module-type: macro

Use Free Spaced Repetition Scheduler: https://github.com/open-spaced-repetition/free-spaced-repetition-scheduler
\*/
(function () {

    /*jslint node: true, browser: true */
    /*global $tw: false */
    "use strict";

    exports.name = "fsrs";

    exports.params = [
        { name: "title" },
        { name: "grade" }
    ];

    /*
    Run the macro
    */
    exports.run = function (title, grade) {
        grade = (grade == "0" || grade == "1" || grade == "2") ? Number(grade) : -1;//Ratings for review have 0, 1, 2. Other ratings mean learn new tiddler.

        var fsrsData = $tw.wiki.getTiddlerData('$:/plugins/oflg/fishing/data');

        function faultToleranceValue(value, defaultValue) {
            return (typeof value === 'number' && !isNaN(value)) ? value : defaultValue;
        }

        var increaseFactor = 60,
            difficultyDecay = -0.7,
            stabilityDecay = -0.2,
            requestRetention = faultToleranceValue(fsrsData.requestRetention, 0.9),
            totalCase = faultToleranceValue(fsrsData.totalCase, 0),
            totalDiff = faultToleranceValue(fsrsData.totalDiff, 0),
            totalReview = faultToleranceValue(fsrsData.totalReview, 0),
            defaultDifficulty = faultToleranceValue(fsrsData.defaultDifficulty, 5),
            defaultStability = faultToleranceValue(fsrsData.defaultStability, 2),
            stabilityDataArry = fsrsData.stabilityDataArry || [];

        requestRetention = requestRetention >= 0.75 && requestRetention <= 0.95 ? requestRetention : 0.9;

        var due, interval, difficulty, stability, retrievability, lapses, reps, review, history;

        review = new Date().toISOString().replace(/\-|T|:|\.|Z/g, "");

        if (grade == -1) {// learn new tiddler
            var addDay = Math.round(defaultStability * Math.log(requestRetention) / Math.log(0.9));

            due = $tw.wiki.filterTiddlers("[[" + addDay + "]due[]]")[0];
            interval = 0;
            difficulty = defaultDifficulty;
            stability = defaultStability;
            retrievability = 1;
            grade = -1;
            reps = 1;
            lapses = 0;
            history = "[]";
        } else {// review tiddler after learn
            var lastFieldsData = $tw.wiki.getTiddler(title).fields;

            var lastDue = String(lastFieldsData.due),
                lastInterval = Number(lastFieldsData.interval),
                lastDifficulty = Number(lastFieldsData.difficulty),
                lastStability = Number(lastFieldsData.stability),
                lastRetrievability = Number(lastFieldsData.retrievability),
                lastGrade = Number(lastFieldsData.grade),
                lastLapses = Number(lastFieldsData.lapses),
                lastReps = Number(lastFieldsData.reps),
                lastReview = String(lastFieldsData.review),
                lastHistory = JSON.parse(lastFieldsData.history);

            interval = Number($tw.wiki.filterTiddlers("[[" + lastReview + "]interval[]]")[0]);
            retrievability = Math.exp(Math.log(0.9) * interval / lastStability);
            difficulty = Math.min(Math.max(lastDifficulty + retrievability - grade + 0.2, 1), 10);

            if (grade == 0) {
                stability = defaultStability * Math.exp(-0.3 * (lastLapses + 1));

                if (lastReps > 1) {
                    totalDiff = totalDiff - retrievability;
                }
                lapses = lastLapses + 1;
                reps = 1;
            } else {//grade == 1 || grade == 2
                stability = lastStability * (1 + increaseFactor * Math.pow(difficulty, difficultyDecay) * Math.pow(lastStability, stabilityDecay) * (Math.exp(1 - retrievability) - 1));

                if (lastReps > 1) {
                    totalDiff = totalDiff + 1 - retrievability;
                }
                lapses = lastLapses;
                reps = lastReps + 1;
            }

            totalCase = totalCase + 1;
            totalReview = totalReview + 1;

            var addDay = Math.round(stability * Math.log(requestRetention) / Math.log(0.9));

            due = $tw.wiki.filterTiddlers("[[" + addDay + "]due[]]")[0];

            lastHistory.push({
                due: lastDue,
                interval: lastInterval,
                difficulty: lastDifficulty,
                stability: lastStability,
                retrievability: lastRetrievability,
                grade: lastGrade,
                lapses: lastLapses,
                reps: lastReps,
                review: lastReview
            });

            history = JSON.stringify(lastHistory);

            // Adaptive defaultDifficulty
            if (totalCase > 100) {
                defaultDifficulty = 1 / Math.pow(totalReview, 0.3) * Math.pow(Math.log(requestRetention) / Math.max(Math.log(requestRetention + totalDiff / totalCase), 0), 1 / difficultyDecay) * 5 + (1 - 1 / Math.pow(totalReview, 0.3)) * defaultDifficulty;

                totalDiff = 0;
                totalCase = 0;
            }

            // Adaptive defaultStability
            if (lastReps === 1 && lastLapses === 0) {
                stabilityDataArry.push({
                    interval: interval,
                    retrievability: grade === "0" ? 0 : 1
                });

                if (stabilityDataArry.length > 0 && stabilityDataArry.length % 50 === 0) {
                    var intervalSetArry = [];

                    var sumRI2S = 0,
                        sumI2S = 0;
                    for (var s = 0; s < stabilityDataArry.length; s++) {
                        var ivl = stabilityDataArry[s].interval;

                        if (intervalSetArry.indexOf(ivl) === -1) {

                            intervalSetArry.push(ivl);

                            var filterArry = stabilityDataArry.filter(function (fi) {
                                return fi.interval === ivl;
                            });

                            var retrievabilitySum = filterArry.reduce(function (sum, e) {
                                return sum + e.retrievability;
                            }, 0);

                            if (retrievabilitySum > 0) {
                                sumRI2S = sumRI2S + ivl * Math.log(retrievabilitySum / filterArry.length) * filterArry.length;
                                sumI2S = sumI2S + ivl * ivl * filterArry.length;
                            }
                        }
                    }
                    defaultStability = (Math.max(Math.log(0.9) / (sumRI2S / sumI2S), 0.1) + defaultStability) / 2;
                }
            }
        }

        var itemData = {
            due: due,
            interval: interval,
            difficulty: difficulty,
            stability: stability,
            retrievability: retrievability,
            lapses: lapses,
            reps: reps,
            grade: grade,
            review: review,
            history: history
        };

        fsrsData = {
            requestRetention: requestRetention,
            increaseFactor: increaseFactor,
            difficultyDecay: difficultyDecay,
            stabilityDecay: stabilityDecay,
            totalCase: totalCase,
            totalDiff: totalDiff,
            totalReview: totalReview,
            defaultDifficulty: defaultDifficulty,
            defaultStability: defaultStability,
            stabilityDataArry: stabilityDataArry
        };

        return { itemData: itemData, fsrsData: fsrsData };// Action was invoked
    };

})();

$:/plugins/oflg/fishing/operations/excis.text:
/*\
title: $:/plugins/oflg/fishing/operations/excisefish.js
type: application/javascript
module-type: texteditoroperation

Text editor operation to excise the selection to a new fishing qa tiddler.
Based on TW's core/modules/editor/operations/text/excise.js

\*/
(function () {

    /*jslint node: true, browser: true */
    /*global $tw: false */
    "use strict";

    exports["excisefish"] = function (event, operation) {
        if (operation.selection) {

            var selectionText = operation.selection,
                exciseCaption = event.paramObject.exciseCaption;

            var editTiddlerTitle = this.editTitle;

            var editTiddler = $tw.wiki.getTiddler(editTiddlerTitle);

            if (editTiddler && editTiddler.fields["draft.title"]) {//is draft tiddler
                editTiddlerTitle = editTiddler.fields["draft.title"];
            }

            var tags = [editTiddlerTitle, "?"],
                priority = editTiddler.fields["priority"] || 0;

            var rTitle = editTiddlerTitle + "/" + new Date(new Date().getTime()).toISOString().replace(/-|T|:|\.|Z/g, "");

            if (exciseCaption === "{{||Excerpt}}") { //to excerpt tiddler

                var title = selectionText.indexOf("''__") !== -1 ? selectionText.split("''__")[0].split("__''").slice(-1)[0] : rTitle,
                    text = selectionText.indexOf("''__") !== -1 ? selectionText.replace(title + "''__", "").replace("__''", "") : operation.selection,
                    caption = "{{||Excerpt}}",
                    interval = Number($tw.wiki.getTiddler("$:/plugins/oflg/fishing/data").fields.requestInterval),
                    due = $tw.wiki.filterTiddlers("[[" + interval + "]due[]]")[0];

                title = $tw.wiki.generateNewTitle(title.replace(/\||\{|\}|\[|\]|\'/g, ""));

                $tw.wiki.addTiddler(
                    new $tw.Tiddler($tw.wiki.getCreationFields(), $tw.wiki.getModificationFields(), {
                        title,
                        text,
                        caption,
                        tags,
                        priority,
                        due,
                        interval
                    })
                );

            } else {// default to question tiddler
                var nTitle = selectionText.split("\n")[0];

                if (nTitle.indexOf("''__") == -1 && nTitle.indexOf("__") !== -1) { //Select or Cloze question tiddler

                    var title = nTitle,
                        text = selectionText.replace(nTitle, "");

                    if (/^[0-9A-I]+$/.test(nTitle.split("__").slice(-1)[0])) {

                        var caption = "{{||Select}}";

                    } else {

                        var caption = "{{||Cloze}}";

                    }

                } else { //Q&A question tiddler

                    var title = selectionText.indexOf("''__") !== -1 ? selectionText.split("''__")[0].split("__''").slice(-1)[0] : rTitle,
                        text = selectionText.indexOf("''__") !== -1 ? selectionText.replace(title + "''__", "").replace("__''", "") : operation.selection,
                        caption = "{{||Question}}";
                }

                title = $tw.wiki.generateNewTitle(title.replace(/\||\{|\}|\[|\]|\'/g, ""));

                $tw.wiki.addTiddler(
                    new $tw.Tiddler($tw.wiki.getCreationFields(), $tw.wiki.getModificationFields(), {
                        title,
                        text,
                        caption,
                        tags,
                        priority
                    })
                );

                $tw.rootWidget.invokeActionString('<$action-fishing $tiddler="""' + title + '""" $grade="learn"/>');
            }

            if (editTiddler.type === "text/x-markdown") {

                operation.replacement = "[[" + title + "]]{{" + title + "}}";

            } else {

                operation.replacement = "\n[[" + title + "]]\n\n<<<.tc-fish-quote\n{{" + title + "}}\n<<<\n\n";

            }

            operation.cutStart = operation.selStart;
            operation.cutEnd = operation.selEnd;
            operation.newSelStart = operation.selStart;
            operation.newSelEnd = operation.selStart + operation.replacement.length;
        }
    };
})();

