$:/plugins/stobot/sticky/stylesheet.text:
.sticky-table {
  border: none;
}

.sticky-table td, th {
  border: 1px solid gainsboro;
}

.sticky-table-invisible {
  border: none;
  margin: 0;
}

.sticky-table-invisible td {
  border: none;
}

.sticky-input {
  width: 100%;
  margin: 0;
  padding: 4px 7px 4px 7px;
  border: 1px solid gainsboro;
}

.sticky-input-short {
  margin: 0;
  padding: 4px 7px 4px 7px;
  border: 1px solid gainsboro;
  text-align: center;
}

.sticky-active {
  {{$:/plugins/stobot/sticky/settings##active-css}}
}

.sticky-done {
  {{$:/plugins/stobot/sticky/settings##done-css}}
}
